import firebaseInIt from "../../Firebase/firebase.init.js";
import { getAuth,signInWithPopup,GoogleAuthProvider,updateProfile,signOut,createUserWithEmailAndPassword} from "firebase/auth";
import { useState } from "react";

firebaseInIt();
const useFirebase = () => {
    const [newUser, setNewUser] = useState({})
    const [user, setUser] = useState({});
    const[error,setError]=useState('')
    const auth = getAuth();
    const googleProvider = new GoogleAuthProvider();

    function googleSignIn() {
        signInWithPopup(auth, googleProvider)
            .then(result => {
                const user = result.user;
                setUser(user);
            })
        .catch(err=>setError(err.message))
    }

    //email-pass
    function registerUser() {
        createUserWithEmailAndPassword(auth, newUser.email, newUser.password)
            .then(result => { })
        .catch(error=>setError(error.message))
    }


    //set user name and photo url
    function setUserDisplayName() {
        updateProfile(auth.currentUser, {
            displayName: newUser.name,
            photoURL:newUser.photoURL
        })
            .then(() => { })
        .ca
    }
    //logout
    function logout() {
        signOut(auth)
            .then(()=>setUser({}))
        .catch(err=>setError(err.message))
    }

    return {
        googleSignIn,
        user, error,
        logout,
        newUser, setNewUser,
        registerUser
}
};

export default useFirebase;
