// import Button from '@restart/ui/esm/Button';
import React, { useState } from 'react';
import { Container, Form,Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import useContexts from '../../Components/hooks/useContexts.js';

const Signup = () => {
  const { authInfo } = useContexts()
  const { newUser, setNewUser,registerUser } = authInfo;
  // const [newUser, setNewUser] = useState({})
  function inputChangeHandler(e) {
    const name = e.target.name;
    const value = e.target.value;
    const extractedUser = { ...newUser }
    extractedUser[name] = value;
    setNewUser(extractedUser);
    console.log(newUser)
  }

  function registerHandler(e) {
    
  }
    return (
        <>
            <Container style={{
                height: "calc(100vh -56px)",
                display: "flex",
                alignItems: "center",
            justifyContent:"center"
            }}>
                <div style={{maxWidth:"500px",width:'100%'}} >
                    <h2 >Register now</h2>
                    <Form>
  <Form.Group className="mb-3" controlId="formBasicName">
    <Form.Label>Your Name</Form.Label>
    <Form.Control type="text" name='name' onChange={inputChangeHandler} placeholder="Enter your name" />
    
                        </Form.Group>

                          <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" onChange={inputChangeHandler} name='email' placeholder="Enter email" />
    
  </Form.Group>

  <Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control onChange={inputChangeHandler} name='password' type="password" placeholder="Password" />
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPhoto">
    <Form.Label>Photo URL</Form.Label>
    <Form.Control onChange={inputChangeHandler} name='photoURL' type="text" placeholder="Enter your photo url" />
  </Form.Group>
  
  <Button variant="primary" className="w-100" type="submit">
    Signup
  </Button>
                    </Form>
                    <p>Already Registerd?<Link to='/login'>Login now</Link></p>
               </div>
            </Container>
        </>
    );
};

export default Signup;
